\COPY (SELECT jsonb_build_object('type',     'FeatureCollection', 'features', jsonb_agg(feature)) FROM (  SELECT jsonb_build_object('type', 'Feature',    'id',         gid,    'geometry',   ST_AsGeoJSON(geom)::jsonb,  'properties', to_jsonb(row) - 'gid' - 'geom'  ) AS feature FROM (SELECT * FROM gis.centreline ) row) features ) TO STDOUT WITH (HEADER FALSE)
